CREATE TABLE mtorg_db.system_plugin_history
(
    id int(10) unsigned PRIMARY KEY NOT NULL AUTO_INCREMENT,
    code varchar(191) NOT NULL,
    type varchar(20) NOT NULL,
    version varchar(50) NOT NULL,
    detail text,
    created_at timestamp
);
CREATE INDEX system_plugin_history_code_index ON mtorg_db.system_plugin_history (code);
CREATE INDEX system_plugin_history_type_index ON mtorg_db.system_plugin_history (type);
